const { readFileSync } = require("fs");
const { createServer } = require("http");
const { parse } = require("url");
const { renderToString } = require("react-dom/server")
const React = require("react")

const pizzas = [
    {
        name: "Focaccia",
        price: 6,
    },
    {
        name: "Pizza Margherita",
        price: 10,
    },
    {
        name: "Pizza Spinaci",
        price: 12,
    },
    {
        name: "Pizza Funghi",
        price: 12,
    },
    {
        name: "Pizza Prosciutto",
        price: 15,
    },
];

function Home() {
    return (
        <div>
            <h1>üçï Fast React Pizza Co.</h1>
            <p>This page has been rendered with React on the server ü§Ø</p>

            <h2>Menu</h2>
            <ul>
                {pizzas.map((pizza) => (
                    <MenuItem pizza={pizza} key={pizza.name} />
                ))}
            </ul>
        </div>
    );
}

function Counter() {
    const [count, setCount] = React.useState(0);

    return (
        <div>
            <button onClick={() => setCount((c) => c + 1)}>+1</button>
            <span>{count}</span>
        </div>
    );
}

function MenuItem({ pizza }) {
    return (
        <li>
            <h4>
                {pizza.name} (${pizza.price})
            </h4>
            <Counter />
        </li>
    );
}




const htmlTemple = readFileSync(`${__dirname}/index.html`, 'utf-8')

const server = createServer((req, res) => {
    const { pathname } = parse(req.url, true);

    if (pathname === "/") {
        const rennderReact = renderToString(<Home />)
        const html = htmlTemple.replace("%%CONTENT%%", rennderReact)
        res.writeHead(200, { "Content-type": "text/html" });
        res.end(html);
    } else if (pathname === "/test") {
        res.end("TEST");
    } else {
        res.end("THE URL CANNOT BE FOUND");
    }
});

server.listen(8000, () => console.log("Listening for request on port 8000"));
